import React from "react";
import { Event } from "../../types/event";
import {
  Calendar as CalendarIcon,
  Clock as ClockIcon,
  DollarSign as DollarIcon,
  MapPin as MapPinIcon,
} from "lucide-react";

interface Props {
  event: Event;
}

const EventCard: React.FC<Props> = ({ event }) => {
  // מוצאים שדות עזר מתוך event
  const coverImage = event.header.images.find((i) => i.type === "cover")?.src;
  const thumbImage = event.header.images.find(
    (i) => i.type === "thumbnail"
  )?.src;
  const date = new Date(event.activityBlock.startDate);
  const dayName = date.toLocaleDateString("he-IL", { weekday: "short" }); // "יום ראשון" -> "א׳"
  const dayNum = date.getDate();

  // מוצאים content.description
  const descriptionItem = event.content.find((c) => c.key === "description");
  const description = descriptionItem?.value.replace(/<[^>]+>/g, "") || "";

  // משתתפים
  const { participantsCount, maxParticipants } = event.people.numbers;

  return (
    <div className="relative group bg-white rounded-2xl shadow-md overflow-hidden">
      {/* תמונת cover */}
      <div className="relative h-48">
        <img
          src={coverImage || thumbImage || "/placeholder.jpg"}
          alt={event.name}
          className="w-full h-full object-cover"
        />

        {/* בועת משתתפים */}
        <div className="absolute bottom-2 left-2 bg-red-600 text-white text-sm font-medium px-2 py-1 rounded-full">
          +{maxParticipants}/{participantsCount ?? 0}
        </div>

        {/* overlay CTA */}
        <div className="absolute inset-0 bg-black bg-opacity-0 group-hover:bg-opacity-50 transition">
          <button className="opacity-0 group-hover:opacity-100 transition absolute inset-0 m-auto w-32 h-10 bg-blue-600 text-white rounded-full">
            הרשמה
          </button>
        </div>
      </div>

      <div className="p-4 flex">
        {/* תאריך בצד שמאל */}
        <div className="flex-shrink-0 mr-4">
          <div className="bg-white text-center shadow rounded-lg">
            <div className="text-xs text-gray-500 pt-1">{dayName}</div>
            <div className="text-xl font-bold">{dayNum}</div>
          </div>
        </div>

        {/* פרטי האירוע */}
      </div>
    </div>
  );
};

export default React.memo(EventCard);
