// src/components/EventCard/EventCard.tsx
import React from "react";
import { Event } from "../../types/event";
import {
  Calendar as CalendarIcon,
  Clock as ClockIcon,
  DollarSign as DollarIcon,
  MapPin as MapPinIcon,
} from "lucide-react";

interface Props {
  event: Event;
}

const EventCard: React.FC<Props> = ({ event }) => {
  // מוצאים שדות עזר מתוך event
  const coverImage = event.header.images.find((i) => i.type === "cover")?.src;
  const thumbImage = event.header.images.find(
    (i) => i.type === "thumbnail"
  )?.src;
  const date = new Date(event.activityBlock.startDate);
  const dayName = date.toLocaleDateString("he-IL", { weekday: "short" }); // "יום ראשון" -> "א׳"
  const dayNum = date.getDate();

  // מוצאים content.description
  const descriptionItem = event.content.find((c) => c.key === "description");
  const description = descriptionItem?.value.replace(/<[^>]+>/g, "") || "";

  // משתתפים
  const { participantsCount, maxParticipants } = event.people.numbers;

  return (
    <div className="relative group bg-white rounded-2xl shadow-md overflow-hidden">
      {/* תמונת cover */}
      <div className="relative h-48">
        <img
          src={coverImage || thumbImage || "/placeholder.jpg"}
          alt={event.name}
          className="w-full h-full object-cover"
        />

        {/* בועת משתתפים */}
        <div className="absolute top-2 left-2 bg-red-600 text-white text-sm font-medium px-2 py-1 rounded-full">
          +{maxParticipants}/{participantsCount ?? 0}
        </div>

        {/* בועת המלצה (subscribedText) */}
        <div className="absolute top-2 right-2 bg-white bg-opacity-80 text-black text-xs font-semibold px-2 py-1 rounded-full">
          {event.header.subscribedText}
        </div>

        {/* overlay CTA */}
        <div className="absolute inset-0 bg-black bg-opacity-0 group-hover:bg-opacity-50 transition">
          <button className="opacity-0 group-hover:opacity-100 transition absolute inset-0 m-auto w-32 h-10 bg-blue-600 text-white rounded-full">
            הרשמה
          </button>
        </div>
      </div>

      <div className="p-4 flex">
        {/* תאריך בצד שמאל */}
        <div className="flex-shrink-0 mr-4">
          <div className="bg-white text-center shadow rounded-lg">
            <div className="text-xs text-gray-500 pt-1">{dayName}</div>
            <div className="text-xl font-bold">{dayNum}</div>
          </div>
        </div>

        {/* פרטי האירוע */}
        <div className="flex-1 space-y-2">
          {/* כותרת */}
          <h3 className="text-lg font-semibold leading-snug">{event.name}</h3>

          {/* ימים (אם רלוונטי) */}
          {event.activityBlock.date && (
            <div className="flex items-center text-sm text-gray-600">
              <CalendarIcon size={16} className="mr-1" />
              <span>{dayName}</span>
            </div>
          )}

          {/* שעות */}
          <div className="flex items-center text-sm text-gray-600">
            <ClockIcon size={16} className="mr-1" />
            <span>
              {new Date(event.activityBlock.startDate).toLocaleTimeString(
                "he-IL",
                {
                  hour: "2-digit",
                  minute: "2-digit",
                }
              )}
              &nbsp;-&nbsp;
              {new Date(event.activityBlock.endDate).toLocaleTimeString(
                "he-IL",
                {
                  hour: "2-digit",
                  minute: "2-digit",
                }
              )}
            </span>
          </div>

          {/* מחיר */}
          <div className="flex items-center text-sm text-gray-600">
            <DollarIcon size={16} className="mr-1" />
            <span>{event.paymentText || "חינם"}</span>
          </div>

          {/* מיקום */}
          {event.activityBlock.location.text && (
            <div className="flex items-center text-sm text-gray-600">
              <MapPinIcon size={16} className="mr-1" />
              <span>{event.activityBlock.location.text}</span>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default React.memo(EventCard);
