// src/components/EventCard/EventCard.tsx
import React from "react";
import { Event } from "../../types/event";

interface Props {
  event: Event;
}

const EventCard: React.FC<Props> = ({ event }) => (
  <div className="relative group bg-white shadow rounded overflow-hidden">
    {/* התמונה */}
    <img
      src={
        event.header.images.find((img) => img.type === "cover")?.src ||
        "/placeholder.jpg"
      }
      alt={event.name}
      className="w-full h-48 object-cover"
    />

    {/* ה־overlay שמופיע ב־hover */}
    <div className="absolute inset-0 bg-black bg-opacity-50 opacity-0 group-hover:opacity-100 transition-opacity flex items-center justify-center">
      <button className="px-4 py-2 bg-red-500 text-white font-semibold rounded">
        הרשמה
      </button>
    </div>

    {/* התוכן מתחת לתמונה */}
    <div className="p-4">
      <h2 className="font-semibold text-lg mb-2">{event.name}</h2>
      <p className="text-sm text-gray-600 line-clamp-2">{event.description}</p>
      {/* שדות נוספים: תאריך, מיקום, מחיר וכו׳ */}
    </div>
  </div>
);

export default React.memo(EventCard);
