import React, { useRef, useEffect, useState, useMemo } from 'react';
import { useFetchEvents } from '../../hooks/useFetchEvents';
import EventGrid from '../EventGrid/EventGrid';
import SearchBar from '../SearchBar/SearchBar';
import { useDebounce } from '../../hooks/useDebounce';

const InfiniteLoader: React.FC = () => {
  const { data, fetchNextPage, hasNextPage, isFetching, isFetchingNextPage } = useFetchEvents();
  const [search, setSearch] = useState('');
  const debouncedSearch = useDebounce(search, 300);

  const allEvents = useMemo(() => data?.pages.flatMap(p => p.items) ?? [], [data]);

  const filtered = useMemo(() => {
    if (!debouncedSearch) return allEvents;
    const term = debouncedSearch.toLowerCase();
    return allEvents.filter(ev =>
      ev.title.toLowerCase().includes(term) ||
      ev.description.toLowerCase().includes(term)
    );
  }, [allEvents, debouncedSearch]);

  const sentinelRef = useRef<HTMLDivElement>(null);
  useEffect(() => {
    if (!sentinelRef.current || !hasNextPage) return;
    const obs = new IntersectionObserver(entries => {
      if (entries[0].isIntersecting) fetchNextPage();
    });
    obs.observe(sentinelRef.current);
    return () => obs.disconnect();
  }, [fetchNextPage, hasNextPage]);

  return (
    <>
      <SearchBar value={search} onChange={setSearch} />
      <EventGrid events={filtered} />
      <div ref={sentinelRef} className="h-8">
        {isFetchingNextPage && <p className="text-center">Loading more…</p>}
      </div>
      {isFetching && !isFetchingNextPage && <p className="text-center">Refreshing…</p>}
    </>
  );
};

export default InfiniteLoader;
